import React from 'react';
import Navbar from 'react-bootstrap/Navbar';
import { Nav, Container, Card, Col } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import DropdownButton from 'react-bootstrap/DropdownButton';

class NavHeader extends React.Component {

	render() {

		return (
			<Navbar expand="lg" className="navbar-sticky">
				<Container
					fluid
					style={{
						fontFamily: 'Poppins',
						fontSize: '14px',
						color: '#585858',
					}}
				>
					<div className="navbar-wrapper">
						<Navbar.Brand
							style={{ cursor: 'pointer' }}
							onClick={() =>
								this.setState({ selectedItem: '' }, () => {
									this.props.history.push('/home');
								})
							}
						>
							<img width="185px" height="29px" src={sublogo} alt="" />
						</Navbar.Brand>
					</div>
					<Navbar.Toggle>
						<span className="navbar-toggler-bar navbar-kebab" />
						<span className="navbar-toggler-bar navbar-kebab" />
						<span className="navbar-toggler-bar navbar-kebab" />
					</Navbar.Toggle>
					<Navbar.Collapse className="justify-content-start">
						<Nav>
							<Nav.Link
								as={Link}
								to="/savedMeeting"
								className={
									this.state.selectedItem == 'Meetings'
										? 'navlink_active'
										: 'navlink_inactive'
								}
								onClick={() => this.setState({ selectedItem: 'Meetings' })}
							>
								<span>mainTitle</span>
							</Nav.Link>

							<Nav.Link
								as={Link}
								to="/upcommingMeeting"
								className={
									this.state.selectedItem == 'Upcoming meeting'
										? 'navlink_active'
										: 'navlink_inactive'
								}
								onClick={() => this.setState({ selectedItem: 'Upcoming meeting' })}
							>
								<span> schedule</span>
							</Nav.Link>
							<Nav.Link
								as={Link}
								to="/contact"
								className={
									this.state.selectedItem == 'Contact'
										? 'navlink_active'
										: 'navlink_inactive'
								}
								onClick={() => this.setState({ selectedItem: 'Contact' })}
							>
								<span> contact</span>
							</Nav.Link>

							<Nav.Link
								as={Link}
								className={
									this.state.selectedItem == 'seletedLanguage'
										? 'navlink_active'
										: 'navlink_inactive'
								}
								onClick={() =>
									this.setState({
										languagesFlag: true,
										selectedItem: 'seletedLanguage',
									})
								}
							>
													<span>
													lang

													</span>

							</Nav.Link>
						</Nav>
					</Navbar.Collapse>
					<Navbar.Collapse className="justify-content-end">
						<Nav>
							<Nav.Link
								as={Link}
								to="/meetnow"
								className={
									this.state.selectedItem == 'Create'
										? 'navlink_active'
										: 'navlink_inactive'
								}
								onClick={() =>
									this.setState({
										selectedItem: 'Create',
									})
								}
							>
								<span>  meetnow</span>
							</Nav.Link>

							<Nav.Link
								as={Link}
								to="/joinRoom"
								className={
									this.state.selectedItem == 'Join'
										? 'navlink_active'
										: 'navlink_inactive'
								}
								onClick={() =>
									this.setState({
										selectedItem: 'Join',
									})
								}
							>
								<span> join </span>
							</Nav.Link>
							<Nav.Link
								as={Link}
								to="/scheduleMeeting"
								className={
									this.state.selectedItem == 'Schedule'
										? 'navlink_active'
										: 'navlink_inactive'
								}
								onClick={() =>
									this.setState({
										selectedItem: 'Schedule',
									})
								}
							>
								<span> meet</span>
							</Nav.Link>

								<div className="image-cropper">
									<img
										src={BaseService.getProfileImage() + this.props.imgSrc}
										className={'py-1 px-1'}
									/>
								</div>

							<DropdownButton
								title={this.props.firstName}
								style={{ alignSelf: 'center' }}
							>
								<Nav.Link
									as={Link}
									to="/myWallet"
									style={{fontFamily: 'Poppins', fontSize: '14px', color: this.state.myWalletBalance===0 ? 'red' : '#668EC7'}}
									className={this.state.selectedItem == 'myWallet' ? 'navlink_active' : 'navlink_inactive'}
									onClick={() => this.setState({selectedItem: 'myWallet',})}
								>
									<span><FontAwesomeIcon icon={faWallet}/>&nbsp;</span><span>{intl.formatMessage({id: 'wallet.mainTitle'})}</span>{': $'}{this.state.myWalletBalance?.toFixed(2)}
								</Nav.Link>
								<Nav.Link
									as={Link}
									to="/userProfile"
									style={{
										fontFamily: 'Poppins',
										fontSize: '14px',
										color: '#585858',
									}}
									className={
										this.state.selectedItem == 'Profile'
											? 'navlink_active'
											: 'navlink_inactive'
									}
									onClick={() =>
										this.setState({
											selectedItem: 'Profile',
										})
									}
								>
									<span><FontAwesomeIcon icon={faUser} color={'#58585870'}/>&nbsp;</span><span>{intl.formatMessage({ id: 'profile.profile' })}</span>
								</Nav.Link>
								<Nav.Link
									as={Link}
									to="/phonebook"
									style={{
										fontFamily: 'Poppins',
										fontSize: '14px',
										color: '#585858',
									}}
									className={
										this.state.selectedItem == 'phoneBook'
											? 'navlink_active'
											: 'navlink_inactive'
									}
									onClick={() =>
										this.setState({
											selectedItem: 'phoneBook',
										})
									}
								>
									<span><FontAwesomeIcon icon={faPhoneSquare} color={'#58585870'}/>&nbsp;</span>
									<span>{intl.formatMessage({ id: 'phoneBook.contactList' })}</span>
								</Nav.Link>
								{/* </Dropdown.Item> */}
								{/* <Dropdown.Item
													onClick={() => this.setState({ show: true, dropDownFlag: true })}
													style={{
														fontFamily: 'Poppins',
														fontSize: '14px',
														color: '#585858',
													}}
												>
													{intl.formatMessage({ id: 'logout.tooltip' })}
												</Dropdown.Item> */}
								<Nav.Link
									as={Link}
									onClick={() =>
										this.setState({ show: true, dropDownFlag: true })
									}
									style={{
										fontFamily: 'Poppins',
										fontSize: '14px',
										color: '#585858',
									}}
								>
									<span><FontAwesomeIcon icon={faPowerOff} color={'#58585870'}/>&nbsp;</span><span>{intl.formatMessage({ id: 'logout.tooltip' })}</span>
								</Nav.Link>
							</DropdownButton>
						</Nav>
					</Navbar.Collapse>
				</Container>
			</Navbar>
		);
	}
}

export default NavHeader;
